#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="E:\Coding\socketio-boilerplate\node_modules\.pnpm\vite-node@3.2.4_@types+node_4f8391a1eef3fa0e69107bb04cf64cea\node_modules\vite-node\node_modules;E:\Coding\socketio-boilerplate\node_modules\.pnpm\vite-node@3.2.4_@types+node_4f8391a1eef3fa0e69107bb04cf64cea\node_modules;E:\Coding\socketio-boilerplate\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/proc/cygdrive/e/Coding/socketio-boilerplate/node_modules/.pnpm/vite-node@3.2.4_@types+node_4f8391a1eef3fa0e69107bb04cf64cea/node_modules/vite-node/node_modules:/proc/cygdrive/e/Coding/socketio-boilerplate/node_modules/.pnpm/vite-node@3.2.4_@types+node_4f8391a1eef3fa0e69107bb04cf64cea/node_modules:/proc/cygdrive/e/Coding/socketio-boilerplate/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../vite-node/vite-node.mjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../vite-node/vite-node.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../vite-node/vite-node.mjs" $args
  } else {
    & "node$exe"  "$basedir/../vite-node/vite-node.mjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
